#Google Cloud Healthcare API Host
export gcp_cloud_healthcare_host="healthcare.googleapis.com"
#Google Cloud Healthcare API Port
export gcp_cloud_healthcare_port="443"
#Google Cloud Healthcare API Path
export gcp_cloud_healthcare_path="/"
#Google Cloud Healthcare API version (v1, v1beta1)
export gcp_api_version="v1beta1"

#Client-Id of the Oauth2.0 Web Client created under Credentials page in your GCP Project
export oauth2_web_client_id="<OAuth2.0 Client ID>"
#Redirect URI of the OAuth2.0 Web client whose Client-Id is being used above
export oauth2_redirect_url="https://<apigee-org>-<apigee-env>.apigee.net/openid/implicit"
#Authorized domain of the App registered under OAuth Consent Screen
export oauth2_redirect_domain="apigee.net"

#Organization where the deployment needs to be done
export apigee_org="<apigee-org>"
#Environment within Organization(mentioned above) where the deployment needs to be done
export apigee_env="<apigee-env>"
#Email of the Apigee user to be used for Approval workflow and creating default apps
export app_manager_email="<app-manager-email>"
#Email of the Product owner to whom approve / reject / cancel email is sent
export product_owner_email="<product-manager-email>"

#Url of the R4 FHIR Store along with the Cloud API Version
export sandbox_r4_fhirstore="/v1beta1/projects/<gcp-project>/location/<region>/dataset/<healthcare-dataset>/fhirstore/<r4-fhir-store>/fhir"
#Full proxy URL where R4 sandbox will be available (example - https://ORG-ENV.apigee.net/r4/sandbox_r4). NOTE - change only ORG and ENV (/r4/sandbox_r4 is fixed and should be kept same)
export sandbox_r4_fhirproxyurl="https://<apigee-org>-<apigee-env>.apigee.net/r4/sandbox_r4"
#Url of the STU3 FHIR Store along with the Cloud API Version
export sandbox_stu3_fhirstore="/v1beta1/projects/<gcp-project>/location/<region>/dataset/<healthcare-dataset>/fhirstore/<stu3-fhir-store>/fhir"
#Full proxy URL where STU3 sandbox will be available (example - https://ORG-ENV.apigee.net/stu3/sandbox_stu3). NOTE - change only ORG and ENV (/stu3/sandbox_stu3 is fixed and should be kept same)
export sandbox_stu3_fhirproxyurl="https://<apigee-org>-<apigee-env>.apigee.net/stu3/sandbox_stu3"
#Url of the DSTU2 FHIR Store along with the Cloud API Version
export sandbox_dstu2_fhirstore="/v1beta1/projects/<gcp-project>/location/<region>/dataset/<healthcare-dataset>/fhirstore/<dstu2-fhir-store>/fhir"
#Full proxy URL where DSTU2 sandbox will be available (example - https://ORG-ENV.apigee.net/dstu2/sandbox_dstu2). NOTE - change only ORG and ENV (/dstu2/sandbox_dstu2 is fixed and should be kept same)
export sandbox_dstu2_fhirproxyurl="https://<apigee-org>-<apigee-env>.apigee.net/dstu2/sandbox_dstu2"

#Apigee Management API Url
export apigeeUrl=https://api.enterprise.apigee.com/v1/organizations

export common_scopes=\"fhirUser\",\"openid\",\"launch/patient\",\"online_access\",\"offline_access\"
export wildcard_scopes=\"patient/*.*\",\"patient/*.read\",\"patient/*.write\",\"user/*.*\",\"user/*.read\",\"user/*.write\",\"system/*.*\",\"system/*.read\",\"system/*.write\"

